mapper.select:
SELECT cm.*, cc.company_name FROM cd_member cm left join cd_company cc on cm.cd_company_id = cc.id
AND cm.member_code LIKE CONCAT('%', #{keyword}, '%') or cm.name LIKE CONCAT('%', #{keyword}, '%') or cm.phone_number like CONCAT('%', #{keyword}, '%')
AND cm.id = #{id}
AND cm.phone_number = #{phoneNumber}
