mapper.select:
SELECT sp.SHOW_VALUE AS orderTypeDesc, tb.* FROM tb_order tb LEFT JOIN sys_dictionary s on s.DICTIONARY_CODE='ORDER_TYPE' left join sys_dictionary_parameter sp on sp.SYS_DICTIONARY_ID=s.ID and tb.ORDER_TYPE = sp.REAL_VALUE
AND tb.order_no LIKE CONCAT('%', #{keyword}, '%') or tb.consigneePhone LIKE CONCAT('%', #{keyword}, '%') or tb.consigneeName like CONCAT('%', #{keyword}, '%')
AND tb.id = #{id}
