mapper.select:
SELECT torp.REF_VALUE as couponNumber, sp.SHOW_VALUE AS orderTypeDesc, ssp.SHOW_VALUE AS orderStatusDesc, paymentp.SHOW_VALUE AS paymentModeDesc, osp.SHOW_VALUE AS orderSourceDesc, st.EXECUTOR_ID AS packingTaskUserId, st.CREATE_TIME as packingTaskCreateTime, st.START_TIME as packingTaskStartTime, st.FINISH_TIME as packingTaskEndTime, u.realname AS packingTaskUsername, st.TASK_STATUS as packingTaskStatus, stsdp.show_value as packingTaskStatusDesc, ce.express_name, users.realname as manager_name, tb.* FROM tb_order tb LEFT JOIN sys_dictionary s on s.DICTIONARY_CODE='ORDER_TYPE' left join sys_dictionary_parameter sp on sp.SYS_DICTIONARY_ID=s.ID and tb.ORDER_TYPE = sp.REAL_VALUE
LEFT JOIN sys_dictionary ss on ss.DICTIONARY_CODE='ORDER_STATUS' left join sys_dictionary_parameter ssp on ssp.SYS_DICTIONARY_ID=ss.ID and tb.ORDER_STATUS = ssp.REAL_VALUE
LEFT JOIN sys_dictionary payment on payment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter paymentp on paymentp.SYS_DICTIONARY_ID=payment.ID and tb.PAYMENT_MODE = paymentp.REAL_VALUE
LEFT JOIN sys_dictionary oss on oss.DICTIONARY_CODE='ORDER_SOURCE' left join sys_dictionary_parameter osp on osp.SYS_DICTIONARY_ID=oss.ID and tb.ORDER_SOURCE = osp.REAL_VALUE
LEFT JOIN tb_order_relation tor ON tor.ORDER_NO = tb.ORDER_NO AND tor.REF_TYPE = 'ORDER_PACKING_TASK_RELATION' LEFT JOIN tb_order_relation torp ON torp.ORDER_NO = tb.ORDER_NO AND torp.REF_TYPE = 'ORDER_PAY_RELATION' LEFT JOIN sys_task st on st.ID= tor.REF_VALUE LEFT JOIN sys_users u ON u.id = st.EXECUTOR_ID LEFT JOIN sys_dictionary stsd on stsd.DICTIONARY_CODE='JOB_STATUS' left join sys_dictionary_parameter stsdp on stsdp.SYS_DICTIONARY_ID=stsd.ID and st.TASK_STATUS = stsdp.REAL_VALUE left join cd_express ce on tb.despatch_express_id = ce.id
left join sys_users users on users.id = tb.MANAGER_ID
AND tb.order_status = #{orderStatus}
AND (tb.order_no LIKE CONCAT('%', #{keyword}, '%') or tb.consignee_phone LIKE CONCAT('%', #{keyword}, '%') or tb.consignee_name like CONCAT('%', #{keyword}, '%') )
AND tb.id = #{id}
AND tb.MANAGER_ID = #{managerId}
AND tb.consignee_name LIKE CONCAT('%', #{consigneeName}, '%')
AND tb.ORDER_NO = #{orderNo}
AND tb.ORDER_NO IN
#{item}
AND st.TASK_STATUS = #{packingTaskStatus}
AND st.EXECUTOR_ID = #{packingTaskUserId}
AND st.EXECUTOR_ID IS NOT NULL
AND st.CREATE_TIME >= #{packingTaskCreateTimeStart}
AND st.CREATE_TIME <= #{packingTaskCreateTimeEnd}
AND tb.create_user_id = #{createUserId}

SELECT p.id as purchaseId, p.PURCHASE_CODE, p.PURCHASE_STATUS, stsdp.show_value as purchaseStatusDesc, p.ITEM_CODE, i.ITEM_NAME, i.PRICING_MANNER AS itemUnit, i.id as cdItemId, i.sell_Unit, i.spec, p.TOTAL AS totalItemQty, p.TOTAL_WEIGHT as totalWeight, p.DISTRIBUTE_VALUE, p.PACKAGE_VALUE, p.PACKING_VALUE FROM tb_purchase p LEFT JOIN cd_item i ON i.ITEM_CODE = p.ITEM_CODE LEFT JOIN sys_dictionary stsd on stsd.DICTIONARY_CODE='PURCHASE_STATUS' left join sys_dictionary_parameter stsdp on stsdp.SYS_DICTIONARY_ID=stsd.ID and p.PURCHASE_STATUS = stsdp.REAL_VALUE
AND ( i.ITEM_NAME LIKE CONCAT('%', #{keyword}, '%') OR p.ITEM_CODE LIKE CONCAT('%', #{keyword}, '%') )
AND p.PURCHASE_STATUS = #{purchaseStatus}
AND p.CREATE_DATE >= #{startTime}
AND p.CREATE_DATE <= #{endTime}
AND (p.TOTAL - p.DISTRIBUTE_VALUE) > 0 AND p.PURCHASE_STATUS = 1
AND i.SELL_UNIT = #{sellUnit}
ORDER BY p.CREATE_DATE DESC

SELECT o.ORDER_NO, SUM(i.ITEM_QTY) AS TOTAL_ITEM_QTY, i.ITEM_ID, i.ITEM_CODE, i.ITEM_UNIT, SUM(i.ITEM_QTY * m.spec) AS TOTAL_WEIGHT FROM tb_order_item i LEFT JOIN tb_order o ON o.ORDER_NO = i.ORDER_NO left join cd_item m ON m.item_code = i.item_code
o.ORDER_STATUS = 1
AND o.CREATE_TIME >= #{startTime}
AND o.CREATE_TIME <= #{endTime}
GROUP BY o.ORDER_NO,i.item_id

SELECT p.ID, p.PACKING_NO, p.PACKING_TIME, p.PACKING_STATUS, tor.ORDER_NO, sdp.SHOW_VALUE AS PACKING_STATUS_desc FROM tb_packing p LEFT JOIN sys_dictionary sd ON sd.DICTIONARY_CODE = 'PACKING_STATUS' LEFT JOIN sys_dictionary_parameter sdp ON sd.ID = sdp.SYS_DICTIONARY_ID AND sdp.REAL_VALUE = p.PACKING_STATUS LEFT JOIN tb_order_relation tor ON tor.REF_TYPE = 'ORDER_PACKING_RELATION' AND tor.REF_VALUE = p.PACKING_NO
AND tor.ORDER_NO = #{orderNo}
AND p.PACKING_NO = #{packingNo}
AND p.PACKING_STATUS = #{packingStatus}

select tt.ITEM_CODE, tt.ITEM_NAME, sum(ITEM_QTY) as itemQty, sum(packedItemQty) as packedItemQty, CASE WHEN sum(packedItemQty) = 0 THEN '未开始' WHEN sum(packedItemQty) = sum(ITEM_QTY) THEN '已完成' ELSE '待完成' END AS packing_status_desc from (SELECT oi.`ITEM_CODE`, oi.ITEM_NAME, oi.ITEM_QTY, COUNT(pr.ID) AS packedItemQty FROM tb_order_item oi LEFT JOIN tb_order o ON o.ORDER_NO = oi.ORDER_NO LEFT JOIN tb_packing_record pr ON pr.ORDER_ITEM_ID = oi.ID LEFT JOIN tb_packing p ON p.ID = pr.PACKING_ID
AND o.id = #{orderId}
AND o.ORDER_NO = #{orderNo}
AND oi.ITEM_CODE = #{itemCode}
AND oi.ORDER_NO IN
#{listItem}
AND oi.id = #{orderItemId}
GROUP BY oi.id) as tt group by tt.ITEM_CODE

SELECT oi.id AS orderItemId, p.PACKING_NO, oi.ITEM_NAME, o.ORDER_NO, oi.ITEM_QTY, COUNT(pr.ID) AS packedItemQty, CASE WHEN COUNT(pr.ID) = 0 THEN '未开始' WHEN COUNT(pr.ID) = oi.item_qty THEN '已完成' ELSE '待完成' END AS packing_status_desc FROM tb_order_item oi LEFT JOIN tb_order o ON o.ORDER_NO = oi.ORDER_NO LEFT JOIN tb_packing_record pr ON pr.ORDER_ITEM_ID = oi.ID LEFT JOIN tb_packing p ON p.ID = pr.PACKING_ID
AND o.id = #{orderId}
AND o.ORDER_NO = #{orderNo}
AND oi.ITEM_CODE = #{itemCode}
AND oi.ORDER_NO IN
#{listItem}
AND oi.id = #{orderItemId}
AND p.PACKING_NO LIKE CONCAT('%', #{keyword}, '%') OR oi.ITEM_NAME LIKE CONCAT('%', #{keyword}, '%')
GROUP BY oi.ID

SELECT oi.ITEM_NAME, tp.PACKING_NO, pr.* FROM tb_packing_record pr LEFT JOIN tb_order_item oi ON oi.ID = pr.ORDER_ITEM_ID left join tb_packing tp on tp.ID = pr.PACKING_ID
AND pr.ID = #{id}
AND pr.ORDER_ITEM_ID = #{orderItemId}
AND ( pr.UPC LIKE CONCAT('%', #{keyword}, '%') OR oi.ITEM_NAME LIKE CONCAT('%', #{keyword}, '%') )
