mapper.select:
SELECT o.ORDER_NO, SUM(i.ITEM_QTY) AS TOTAL_ITEM_QTY, i.ITEM_ID, i.ITEM_CODE, i.ITEM_UNIT, SUM(i.ITEM_QTY * m.spec) AS TOTAL_WEIGHT FROM tb_order_item i LEFT JOIN tb_order o ON o.ORDER_NO = i.ORDER_NO left join cd_item m ON m.item_code = i.item_code
o.ORDER_STATUS = 1
AND o.CREATE_TIME >= #{startTime}
AND o.CREATE_TIME <= #{endTime}
GROUP BY o.ORDER_NO,i.item_id

SELECT p.ID, p.PACKING_NO, p.PACKING_TIME, p.PACKING_STATUS, sdp.SHOW_VALUE AS PACKING_STATUS_desc FROM tb_packing p LEFT JOIN sys_dictionary sd ON sd.DICTIONARY_CODE = 'PACKING_STATUS' LEFT JOIN sys_dictionary_parameter sdp ON sd.ID = sdp.SYS_DICTIONARY_ID AND sdp.REAL_VALUE = p.PACKING_STATUS
AND p.PACKING_NO = #{packingNo}
AND p.PACKING_STATUS = #{packingStatus}
